mapper.insert:
INSERT INTO TB_SAMPLE
(
	USER_NO
,	USER_NAME
,	USER_EMAIL
,	USER_PASSWD
,   REG_DTM
)
VALUES
(
	SEQ_SAMPLE.NEXTVAL
,	#{user_name}
,	#{user_email}
,	#{user_passwd}
,	SYSDATE
)

mapper.update:
UPDATE 	TB_SAMPLE
SET		USER_EMAIL = #{user_email}
,		USER_NAME = #{user_name}
WHERE	USER_NO = #{user_no}
AND		USER_PASSWD = #{user_passwd}

mapper.select:
SELECT  COUNT(1) AS count
FROM	TB_SAMPLE
WHERE	USER_NO = #{user_no}
AND		USER_PASSWD = #{user_passwd}
